			// TODO CREATE NUM_THREADS JOBS OR MORE
			// each thread will take a number of words from the global array and process them

			synchronize_complete( threadpool );

			//fprintf( stderr, "batch words ready in documentWords finished\n" );

			trie_search_job *job;
