void TrieTraverse( TrieNodeDocument* node, unsigned int trie_words_num, unsigned int trie_words_offset ){
	////fprintf( stderr, "[%u]\n", trie_words_offset );
	if( node == 0 )
		return ;
	std::vector<TrieNodeDocument*> stack;
	unsigned int stack_size=1, coffset=trie_words_offset;
	stack.push_back( node );
	TrieNodeDocument* cnode;
	while( stack_size > 0 ){
		stack_size--;
		cnode = stack.back();
		stack.pop_back();
		for( char i=0; i<VALID_CHARS; i++ ){
			if( cnode->children[i] != 0 ){
				stack.push_back(cnode->children[i]);
				stack_size++;
			}
		}
		if( cnode->exists ){
			////fprintf( stderr, "inserting [%u] [%c]\n", coffset, cnode->w[0] );
			documentWords[ coffset++ ] = cnode;
			////fprintf( stderr, "inserted word[%.*s] wsz[%d]\n", documentWords[coffset-1]->wsz, documentWords[coffset-1]->w, documentWords[coffset-1]->wsz );
		}
	}
}

